import { Schematic, Subscription } from './schematic';
export interface Schema extends Schematic {
    authentication: {
        login: {
            username: string;
            password: string;
        };
        encrypt: string;
    };
    client: {
        transactions: {
            userJoined: (name: string) => void;
            userLeft: (name: string) => void;
        };
        requests: {
            getEmail: () => string;
        };
        streams: {
            file: (name: string) => void;
        };
        subscriptions: {
            typing: Subscription<{
                channelId: number;
            }, boolean>;
        };
    };
    server: {
        transactions: {
            setNickname: (name: string) => void;
        };
        requests: {
            getUserList: (channelId: string) => {
                nickname: string;
                status: string;
            }[];
        };
        streams: {
            avatar: (format: string) => void;
        };
        subscriptions: {
            cpu: Subscription<{
                channelId: number;
            }, {
                message: string;
                user: string;
                timestamp: number;
            }>;
        };
    };
}
